prints:
    mov bh, 00h
    mov cx, 01h
    mov ah, 02h
    int 10h
    .loop:
        lodsb
        cmp al, 0
        je .endloop
        mov ah, 09h ; printar o caractere
        mov bl, 15  ; cor: branco
        int 10h

        mov ah, 02h ; move o Cursor
        inc dl
        int 10h

        jmp .loop
    .endloop:
    ret

clear_screen:
    mov ah, 00h
    mov al, 13h
    int 10h
    ret

start:
    xor ax, ax
    mov ds, ax
    mov es, ax

    ; Modo de vídeo
    mov ah, 00h
    mov al, 13h
    int 10h
    jmp print_menu

input_menu:
    mov ah, 0
    int 16h

    cmp ah, 02h
    je select_1_menu

    cmp ah, 03h
    je select_2_menu

    cmp ah, 04h
    je select_3_menu

    jmp input_menu

select_1_menu:
    call clear_screen
    jmp print_game

select_2_menu:
    call clear_screen
    jmp print_tutorial

select_3_menu:
    jmp end

print_menu:
    call clear_screen

    mov dx, 050Ch
    mov si, title
    call prints

    mov dx, 0C0Eh
    mov si, jogarMenu
    call prints

    mov dx, 0E0Dh
    mov si, tutorialMenu
    call prints

    mov dx, 100Fh
    mov si, sairMenu
    call prints

    mov dx, 1809h
    mov si, creditos
    call prints

    jmp input_menu

print_sprite_upper:
    .spriteLoopU:
        cmp cl, bl
        je .endSpriteLoopU
        
        lodsb
        mov ah, 0ch
        int 10h
        inc cx
        jmp .spriteLoopU
    .endSpriteLoopU:

    sub cx, 64
    inc dx
    cmp dl, 64
    jne print_sprite_upper

    ret

print_sprite_middle:
    .spriteLoopM:
        cmp cl, bl
        je .endSpriteLoopM
        
        lodsb
        mov ah, 0ch
        int 10h
        inc cx
        jmp .spriteLoopM
    .endSpriteLoopM:

    sub cx, 64
    inc dx
    cmp dl, 128
    jne print_sprite_middle

    ret

print_sprite_lower:
    .spriteLoopL:
        cmp cl, bl
        je .endSpriteLoopL
        
        lodsb
        mov ah, 0ch
        int 10h
        inc cx
        jmp .spriteLoopL
    .endSpriteLoopL:

    sub cx, 64
    inc dx
    cmp dl, 192
    jne print_sprite_lower

    ret

print_game:
    ; Fileira superior:
    mov bh, 0
    mov bl, 64
    mov cx, 0000h
    mov dx, 0000h
    mov si, AmogusHole
    call print_sprite_upper

    add bl, 64
    mov cx, 0040h
    mov dx, 0000h
    mov si, AmogusHole
    call print_sprite_upper

    add bl, 64
    mov cx, 0080h
    mov dx, 0000h
    mov si, AmogusUp
    call print_sprite_upper

    ; Fileira do meio
    mov bl, 64
    mov cx, 0000h              
    mov dx, 0040h
    mov si, AmogusUp
    call print_sprite_middle

    add bl, 64
    mov cx, 0040h
    mov dx, 0040h
    mov si, AmogusUp
    call print_sprite_middle

    add bl, 64
    mov cx, 0080h
    mov dx, 0040h
    mov si, AmogusUp
    call print_sprite_middle

    ; Fileira inferior
    mov bl, 64
    mov cx, 0000h              
    mov dx, 0080h
    mov si, AmogusUp
    call print_sprite_lower

    add bl, 64
    mov cx, 0040h
    mov dx, 0080h
    mov si, AmogusUp
    call print_sprite_lower

    add bl, 64
    mov cx, 0080h
    mov dx, 0080h
    mov si, AmogusUp
    call print_sprite_lower

    jmp print_game

print_tutorial:

    mov dx, 0000h
    mov si, tutorialStr1
    call prints

    mov dx, 0100h
    mov si, tutorialStr2
    call prints

    mov dx, 0200h
    mov si, tutorialStr3
    call prints
    
    mov ah, 0
    int 16h

    ; retorna para o menu com as seguintes teclas
    cmp ah, 01h ; esc
    je print_menu

    cmp ah, 1ch ; enter
    je print_menu

    cmp ah, 39h ; spacebar
    je print_menu

    jmp print_tutorial

end:
    call clear_screen

    mov dx, 0C10h
    mov si, telaSair
    call prints

jmp $