prints:
    mov bh, 00h
    mov cx, 01h
    mov ah, 02h
    int 10h
    .loop:
        lodsb
        cmp al, 0
        je .endloop
        mov ah, 09h ; printar o caractere
        mov bl, 15  ; cor: branco
        int 10h

        mov ah, 02h ; move o Cursor
        inc dl
        int 10h

        jmp .loop
    .endloop:
    ret

clear_screen:
    mov ah, 00h
    mov al, 13h
    int 10h
    ret

start:
    xor ax, ax
    mov ds, ax
    mov es, ax

    ; Modo de vídeo
    mov ah, 00h
    mov al, 13h
    int 10h
    jmp print_menu

input_menu:
    mov ah, 0
    int 16h

    cmp ah, 02h
    je select_1_menu

    cmp ah, 03h
    je select_2_menu

    cmp ah, 04h
    je select_3_menu

    jmp input_menu

select_1_menu:
    call clear_screen
    jmp print_game

select_2_menu:
    call clear_screen
    jmp print_tutorial

select_3_menu:
    jmp end

print_menu:
    call clear_screen

    mov dx, 050Ch
    mov si, title
    call prints

    mov dx, 0C0Fh
    mov si, jogarMenu
    call prints

    mov dx, 0E0Dh
    mov si, tutorialMenu
    call prints

    mov dx, 100Fh
    mov si, sairMenu
    call prints

    mov dx, 1809h
    mov si, creditos
    call prints

    jmp input_menu

print_sprite_upper:
    .spriteLoopU:
        cmp cl, bl
        je .endSpriteLoopU
        
        lodsb
        mov ah, 0ch
        int 10h
        inc cx
        jmp .spriteLoopU
    .endSpriteLoopU:

    sub cx, 64
    inc dx
    cmp dl, 64
    jne print_sprite_upper

    ret

print_sprite_middle:
    .spriteLoopM:
        cmp cl, bl
        je .endSpriteLoopM
        
        lodsb
        mov ah, 0ch
        int 10h
        inc cx
        jmp .spriteLoopM
    .endSpriteLoopM:

    sub cx, 64
    inc dx
    cmp dl, 128
    jne print_sprite_middle

    ret

print_sprite_lower:
    .spriteLoopL:
        cmp cl, bl
        je .endSpriteLoopL
        
        lodsb
        mov ah, 0ch
        int 10h
        inc cx
        jmp .spriteLoopL
    .endSpriteLoopL:

    sub cx, 64
    inc dx
    cmp dl, 192
    jne print_sprite_lower

    ret

print_line_hor:
    .lineLoopH:
        cmp dl, bl
        je .endLineLoopH

        mov ah, 0ch
        int 10h
        inc dx
        jmp .lineLoopH
    .endLineLoopH:

    sub dx, 2
    inc cx
    cmp cl, 320
    jne print_line_hor

    ret

print_line_ver:
    .lineLoopV:
        cmp cx, bx
        je .endLineLoopV

        mov ah, 0ch
        int 10h
        inc cx 
        jmp .lineLoopV
    .endLineLoopV:

    sub cx, 2
    inc dx
    cmp dl, 192
    jne print_line_ver

    ret

print_game:
    ; Fileira superior:
    mov bh, 0
    mov bl, 64
    mov cx, 0000h
    mov dx, 0000h
    mov si, AmogusHole
    call print_sprite_upper

    add bl, 64
    mov cx, 0040h
    mov dx, 0000h
    mov si, AmogusHole
    call print_sprite_upper

    add bl, 64
    mov cx, 0080h
    mov dx, 0000h
    mov si, AmogusUp
    call print_sprite_upper

    ; Fileira do meio
    mov bl, 64
    mov cx, 0000h              
    mov dx, 0040h
    mov si, AmogusUp
    call print_sprite_middle

    add bl, 64
    mov cx, 0040h
    mov dx, 0040h
    mov si, AmogusUp
    call print_sprite_middle

    add bl, 64
    mov cx, 0080h
    mov dx, 0040h
    mov si, AmogusUp
    call print_sprite_middle

    ; Fileira inferior
    mov bl, 64
    mov cx, 0000h              
    mov dx, 0080h
    mov si, AmogusUp
    call print_sprite_lower

    add bl, 64
    mov cx, 0040h
    mov dx, 0080h
    mov si, AmogusUp
    call print_sprite_lower

    add bl, 64
    mov cx, 0080h
    mov dx, 0080h
    mov si, AmogusUp
    call print_sprite_lower

    mov al, 15
    mov bl, 2
    mov cx, 00C0h
    mov dx, 0000h
    call print_line_hor

    add bl, 192
    mov cx, 00C0h
    mov dx, 00C0h
    call print_line_hor

    mov bl, 194
    mov cx, 00C0h ; horizontal inicial
    mov dx, 0000h ; vertical inicial
    call print_line_ver

    add bx, 126
    mov cx, 0140h ; horizontal inicial
    mov dx, 0000h ; vertical inicial
    call print_line_ver

    mov dx, 021Ch
    mov si, pontuacao
    call prints

    jmp input_game

input_game:
    mov ah, 0
    int 16h

    cmp ah, 10h
    je pressQ

    cmp ah, 11h
    je pressW

    cmp ah, 12h
    je pressE

    cmp ah, 1Eh
    je pressA

    cmp ah, 1Fh
    je pressS

    cmp ah, 20h
    je pressD

    cmp ah, 2Ch
    je pressZ

    cmp ah, 2Dh
    je pressX

    cmp ah, 2Eh
    je pressC

    jmp input_game

pressQ:
    ; fazer as checagens de hitbox
    jmp print_menu

pressW:
    jmp print_menu

pressE:
    jmp print_menu

pressA:
    jmp print_menu
    
pressS:
    jmp print_menu
    
pressD:
    jmp print_menu
    
pressZ:
    jmp print_menu
    
pressX:
    jmp print_menu
    
pressC:
    jmp print_menu
    
print_tutorial:

    mov dx, 0000h
    mov si, tutorialStr1
    call prints

    mov dx, 0100h
    mov si, tutorialStr2
    call prints

    mov dx, 0200h
    mov si, tutorialStr3
    call prints
    
    mov ah, 0
    int 16h

    ; retorna para o menu com as seguintes teclas
    cmp ah, 01h ; esc
    je print_menu

    cmp ah, 1ch ; enter
    je print_menu

    cmp ah, 39h ; spacebar
    je print_menu

    jmp print_tutorial

end:
    call clear_screen

    mov dx, 0C11h
    mov si, telaSair
    call prints

jmp $

