randGen:
    mov si,sp
    mov ah, 0
    mov al, [si+24];Pega Xn da memória
    mov dl,4 ;Pega constante "a" 
    mul dl 
    mov dl,4 ;Pega constante "c"
    add al,dl
    mov dl,9 ;Pega o módulo "m"
    div dl ; Resto se encontra em AH
    mov [si+24],ah ;Coloca "Xn+1" na memória (Numero gerado)

    mov ah,00h
    int 1ah
    mov al, [si+24]
    mul dl
    mov dl,9
    div dl
    mov dl,ah
    ;Resto no dl (0-8)
    call updateState
    ret

updateState:
    cmp dl, 0
    jne check1
        mov si, sp
        mov al, [si+6]
        cmp al, 0 ; originalmente [si+2], +4 por conta das chamadas de função
        jne checkEnd
        mov al, 1
        mov [si+6], al

    check1:
    cmp dl, 1
    jne check2
        mov si, sp
        mov al, [si+20]
        cmp al, 0 
        jne checkEnd
        mov al, 1
        mov [si+20], al

    check2:
    cmp dl, 2
    jne check3
        mov si, sp
        mov al, [si+16]
        cmp al, 0 
        jne checkEnd
        mov al, 1
        mov [si+16], al

    check3:
    cmp dl, 3
    jne check4
        mov si, sp
        mov al, [si+8]
        cmp al, 0 
        jne checkEnd
        mov al, 1
        mov [si+8], al

    check4:
    cmp dl, 4
    jne check5
        mov si, sp
        mov al, [si+18]
        cmp al, 0 
        jne checkEnd
        mov al, 1
        mov [si+18], al

    check5:
    cmp dl, 5
    jne check6
        mov si, sp
        mov al, [si+10]
        cmp al, 0 
        jne checkEnd
        mov al, 1
        mov [si+10], al

    check6:
    cmp dl, 6
    jne check7
        mov si, sp
        mov al, [si+14]
        cmp al, 0 
        jne checkEnd
        mov al ,1
        mov [si+14], al

    check7:
    cmp dl, 7
    jne check8
        mov si, sp
        mov al, [si+22]
        cmp al, 0 
        jne checkEnd
        mov al, 1
        mov [si+22], al

    check8:
    cmp dl, 8
    jne checkEnd
        mov si, sp
        mov al, [si+12]
        cmp al, 0 
        jne checkEnd
        mov al, 1
        mov [si+12], al
    
    checkEnd:
    ret

prints:
    mov bh, 00h
    mov cx, 01h
    mov ah, 02h
    int 10h
    .loop:
        lodsb
        cmp al, 0
        je .endloop
        mov ah, 09h ; printar o caractere
        mov bl, 15  ; cor: branco
        int 10h

        mov ah, 02h ; move o Cursor
        inc dl
        int 10h

        jmp .loop
    .endloop:
    ret

clear_screen:
    mov ah, 00h
    mov al, 13h
    int 10h
    ret

start:
    xor ax, ax
    mov ds, ax
    mov es, ax

; Armazenamento inicial de variáveis
    push ax ; Quantidade de toupeiras up [+24]

    mov ax, 3
    push ax ; Valor da randGen [+22]

    mov ax, 0
    push ax ; Segundos atuais [+20]

    ; Estados: 0 é abaixado, 1 é levantado
    push ax ; State [2][2] [+18]
    push ax ; State [2][1] [+16]
    push ax ; State [2][0] [+14]
    push ax ; State [1][2] [+12]
    push ax ; State [1][1] [+10]
    push ax ; State [1][0] [+8]
    push ax ; State [0][2] [+6]
    push ax ; State [0][1] [+4]
