printint:
    mov bh, 00h
    mov ah, 02h
    int 10h ; Setar posição do cursor
    mov ah, 0
    mov cx, 1
    .loopInt:
        cmp al, 0
        je .endloopInt
        mov bl, 10
        div bl                  
        mov bx, 0
        mov bl, ah 
        mov ah, 0

        push bx
        inc cx

        jmp .loopInt
    .endloopInt:
    dec cx
    cmp cx, 0
    jne .popInt

    ret

    .popInt:
        pop ax
        add al, 48
        mov ah, 09h ; printar o caractere
        mov bl, 15  ; cor: branco
        int 10h

        mov ah, 02h ; move o Cursor
        inc dl
        int 10h

        jmp .endloopInt

input_game:
    mov ah, 1 ; Confere o estado do buffer sem travar o programa
    int 16h

    jz print_game ; Se o buffer estiver vazio, programa continua

    mov ah, 0 ; Se houver algo no buffer, ele é lido
    int 16h

    cmp ah, 10h
    je pressQ

    cmp ah, 11h
    je pressW

    cmp ah, 12h
    je pressE

    cmp ah, 1Eh
    je pressA

    cmp ah, 1Fh
    je pressS

    cmp ah, 20h
    je pressD

    cmp ah, 2Ch
    je pressZ

    cmp ah, 2Dh
    je pressX

    cmp ah, 2Eh
    je pressC

    jmp print_game

